                for j in range(period, self.dim, period):
                    end_index = min(j + period, self.dim)
                    population[i][j:end_index] = pattern[:end_index - j]
        return population

    def periodic_hill_climb(self, func, individual, period, lb, ub):
        # Periodic hill climbing in small per-period increments
        for start in range(0, self.dim, period):
            end = min(start + period, self.dim)
            segment = individual[start:end]
