        initial_point = (individual + best_solution) / 2 if best_solution is not None else individual
        result = minimize(bounded_func, initial_point, method='L-BFGS-B')
        return result.x

    def improved_periodicity_heuristic(self, population):
        for i in range(self.population_size):
            if np.random.rand() < 0.6:
                period = np.random.randint(1, self.dim // 2)
                pattern = population[i][:period]
                for j in range(period, self.dim, period):
                    end_index = min(j + period, self.dim)
                    population[i][j:end_index] = pattern[:end_index - j]
        return population

    def __call__(self, func):
        lb, ub = func.bounds.lb, func.bounds.ub
        population = self.initialize_population(lb, ub)
        best_solution = None
        best_value = float('inf')
        stagnation_counter = 0  # Initialize stagnation counter

        for generation in range(self.n_generations):
