import numpy as np
from scipy.optimize import minimize
from scipy.stats import norm

class AdaptivePeriodicOptimization:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 10 * dim
        self.fitness = np.inf * np.ones(self.population_size)
        self.bounds = None

    def initialize_population(self, lb, ub):
        return np.random.uniform(lb, ub, (self.population_size, self.dim))

    def periodic_guided_mutation(self, individual, lb, ub, t):
        periodicity = np.sin(2 * np.pi * t / self.budget)
        scale_factor = np.random.uniform(0.4, 0.9)  
        perturbation = scale_factor * periodicity * (ub - lb)
        return np.clip(individual + perturbation, lb, ub)

    def evaluate_population(self, pop):
        return np.array([self.func(ind) for ind in pop])

    def select_best(self, pop, fitness):
        best_idx = np.argmin(fitness)
        return pop[best_idx], fitness[best_idx]

    def bayesian_local_refinement(self, candidate, lb, ub):
        def acquisition(x):
            x = np.atleast_2d(x)
            mu, sigma = self.predict(x)
            return mu - 1.96 * sigma

        bounds = list(zip(lb, ub))
        res = minimize(acquisition, candidate, bounds=bounds, method='L-BFGS-B', options={'maxiter': self.budget // 20})
        return res.x, self.func(res.x)

    def predict(self, x):
        distances = np.linalg.norm(x - self.pop, axis=1)
        weights = norm.pdf(distances)
        weights /= np.sum(weights)
        mu = np.dot(weights, self.fitness)
        sigma = np.sqrt(np.dot(weights, (self.fitness - mu) ** 2))
        return mu, sigma

    def __call__(self, func):
        self.func = func
        self.bounds = func.bounds
        lb, ub = self.bounds.lb, self.bounds.ub

        self.pop = self.initialize_population(lb, ub)
        self.fitness = self.evaluate_population(self.pop)
        evaluations = len(self.pop)

        while evaluations < self.budget:
            for i in range(self.population_size):
                candidate = self.periodic_guided_mutation(self.pop[i], lb, ub, evaluations)
                candidate_fitness = self.func(candidate)
                if candidate_fitness < self.fitness[i]:
                    self.pop[i], self.fitness[i] = candidate, candidate_fitness
                evaluations += 1

            if evaluations < self.budget:
                best_solution, best_fitness = self.select_best(self.pop, self.fitness)
                refined_solution, refined_fitness = self.bayesian_local_refinement(best_solution, lb, ub)
                if refined_fitness < best_fitness:
                    best_solution, best_fitness = refined_solution, refined_fitness
                evaluations += self.budget // 20

        return best_solution

import numpy as np
from scipy.optimize import minimize

class EnhancedDifferentialEvolution:
    def __init__(self, budget, dim, pop_size=50, F=0.8, CR=0.9):
        self.budget = budget
        self.dim = dim
        self.pop_size = pop_size
        self.F = F
        self.CR = CR
        self.evaluations = 0

    def quasi_opposition_based_initialization(self, lb, ub):
        pop = np.random.uniform(lb, ub, (self.pop_size, self.dim))
        pop_opposite = lb + ub - pop
        return np.vstack((pop, pop_opposite))

    def enforce_periodicity(self, candidate):
        period = self.dim // 2
        periodic_candidate = np.tile(candidate[:period], self.dim // period)
        return periodic_candidate[:self.dim]  # Fix potential overrun

    def tune_parameters(self):
        diversity = np.std([np.linalg.norm(ind) for ind in population])
        self.F = 0.5 + (0.5 * diversity / (np.max(fitness) - np.min(fitness)))
        if self.evaluations < self.budget // 3:
            self.CR = 0.85  # Adjusted CR
        elif self.evaluations < 2 * self.budget // 3:
            self.CR = 0.8
        else:
            self.CR = 0.75  # Adjusted CR

    def tailored_crossover(self, mutant, target):
        cross_points = np.random.rand(self.dim) < self.CR
        if not np.any(cross_points):
            cross_points[np.random.randint(0, self.dim)] = True
        return np.where(cross_points, mutant, target)

    def optimize(self, func, lb, ub):
        lb = np.array(lb)
        ub = np.array(ub)
        population = self.quasi_opposition_based_initialization(lb, ub)
        population = population[:self.pop_size]  # Use only pop_size number of initial solutions
        fitness = np.array([func(ind) for ind in population])
        self.evaluations += len(population)

        while self.evaluations < self.budget:
            self.tune_parameters()
            for i in range(self.pop_size):
                if self.evaluations >= self.budget:
                    break
                idxs = [idx for idx in range(self.pop_size) if idx != i]
                a, b, c = population[np.random.choice(idxs, 3, replace=False)]
                mutant = np.clip(a + self.F * (b - c), lb, ub)
                trial = self.tailored_crossover(mutant, population[i])  # Use new crossover
                trial = self.enforce_periodicity(trial)

                f = func(trial)
                self.evaluations += 1

                if f < fitness[i]:
                    fitness[i] = f
                    population[i] = trial

            best_idx = np.argmin(fitness)
            best_solution = population[best_idx]
            res = minimize(func, best_solution, method='L-BFGS-B', bounds=[(lb[i], ub[i]) for i in range(self.dim)])
            if res.fun < fitness[best_idx]:
                population[best_idx] = res.x
                fitness[best_idx] = res.fun

        best_idx = np.argmin(fitness)
        return population[best_idx], fitness[best_idx]

    def __call__(self, func):
        lb, ub = func.bounds.lb, func.bounds.ub
        return self.optimize(func, lb, ub)
