import numpy as np
from collections import deque

class MO_QPSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.swarm_size = 20
        self.swarms = []
        self.phi_base = np.pi / 6
        self.archive = deque(maxlen=50)
        self.diversity_threshold = 0.1

    def initialize_swarm(self, lb, ub):
        swarm = []
        for _ in range(self.swarm_size):
            position = lb + (ub - lb) * np.random.rand(self.dim)
            velocity = np.zeros(self.dim)
            best_position = position.copy()
            best_value = float('inf')
            swarm.append({'position': position, 'velocity': velocity, 'best_position': best_position, 'best_value': best_value})
        return swarm

    def quantum_update(self, particle, global_best, lb, ub):
        for i in range(self.dim):
            r = np.random.rand()
            progress = len(self.archive) / self.archive.maxlen
            phi = self.phi_base * (1 + np.cos(np.pi * progress))
            theta = phi if r < 0.5 else -phi
            particle['velocity'][i] = particle['velocity'][i] * np.cos(theta) + (global_best[i] - particle['position'][i]) * np.sin(theta)
            particle['position'][i] += particle['velocity'][i]

            if particle['position'][i] < lb[i] or particle['position'][i] > ub[i]:
                particle['position'][i] = lb[i] + (ub[i] - lb[i]) * np.random.rand()

        particle['position'] = np.clip(particle['position'], lb, ub)

    def update_archive(self, particle):
        if len(self.archive) < self.archive.maxlen or any(np.linalg.norm(particle['position'] - archived['position']) > self.diversity_threshold for archived in self.archive):
            self.archive.append(particle)

    def pareto_dominance(self, a, b):
        return all(a <= b) and any(a < b)

    def select_global_best(self):
        if not self.archive:
            return None

        non_dominated = [self.archive[0]]
        for candidate in self.archive:
            if any(self.pareto_dominance(candidate['best_value'], other['best_value']) for other in non_dominated):
                continue
            non_dominated = [c for c in non_dominated if not self.pareto_dominance(c['best_value'], candidate['best_value'])]
            non_dominated.append(candidate)

        return non_dominated[np.random.choice(len(non_dominated))]['best_position']

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        self.swarms = self.initialize_swarm(lb, ub)

        while evaluations < self.budget:
            for particle_index, particle in enumerate(self.swarms):
                value = func(particle['position'])
                evaluations += 1

                if value < particle['best_value']:
                    particle['best_value'] = value
                    particle['best_position'] = particle['position'].copy()

                self.update_archive(particle)

                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = particle['position'].copy()

                if evaluations >= self.budget:
                    break

            global_best = self.select_global_best()
            for particle_index, particle in enumerate(self.swarms):
                if global_best is not None:
                    self.quantum_update(particle, global_best, lb, ub)

        return self.best_solution, self.best_value
```

# Description: Introducing Adaptive Hierarchical Clustering Optimization (AHCO) that employs dynamic hierarchical clustering to adaptively manage exploration and exploitation, while refining clusters based on local optima to efficiently navigate complex search landscapes.
# Code:
```python
import numpy as np
from scipy.spatial.distance import cdist

class AHCO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.population_size = 30
        self.clusters = []
        self.cluster_assignments = []

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def adaptive_hierarchy(self, data, n_clusters):
        distances = cdist(data, data)
        clusters = {i: [i] for i in range(len(data))}
        while len(clusters) > n_clusters:
            # find closest pair of clusters
            min_dist = float('inf')
            merge_pair = ()
            for i in clusters:
                for j in clusters:
                    if i < j:
                        dist = np.min(distances[np.ix_(clusters[i], clusters[j])])
                        if dist < min_dist:
                            min_dist = dist
                            merge_pair = (i, j)
            i, j = merge_pair
            # merge clusters
            clusters[i].extend(clusters[j])
            del clusters[j]
        return list(clusters.values())

    def update_clusters(self, population, lb, ub):
        n_clusters = max(2, int(self.population_size * 0.1))
        cluster_indices = self.adaptive_hierarchy(population, n_clusters)
        self.clusters = [population[indices] for indices in cluster_indices]
        self.cluster_assignments = np.zeros(self.population_size, dtype=int)
        for cluster_id, indices in enumerate(cluster_indices):
            for i in indices:
                self.cluster_assignments[i] = cluster_id

    def exploit_cluster(self, cluster, func, lb, ub):
        centroid = np.mean(cluster, axis=0)
        for i in range(len(cluster)):
            perturbation = (np.random.rand(self.dim) - 0.5) * 0.1 * (ub - lb)
            candidate = np.clip(centroid + perturbation, lb, ub)
            if func(candidate) < func(cluster[i]):
                cluster[i] = candidate

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0

        population = self.initialize_population(lb, ub)
        self.update_clusters(population, lb, ub)

        while evaluations < self.budget:
            for i, pos in enumerate(population):
                value = func(pos)
                evaluations += 1

                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = pos.copy()

                if evaluations >= self.budget:
                    break

            self.update_clusters(population, lb, ub)

            for cluster in self.clusters:
                self.exploit_cluster(cluster, func, lb, ub)

        return self.best_solution, self.best_value
```

# Description: Adaptive Differential Evolution with Self-Adaptive Mutation and Crossover (ADESAC) utilizing self-adaptive mechanisms to balance exploration and exploitation for optimizing photonic structures.
# Code: 
```python
import numpy as np

class ADESAC:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.F = 0.5  # initial differential weight
        self.CR = 0.9  # initial crossover probability

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def mutate(self, idx, population, F):
        indices = list(range(self.population_size))
        indices.remove(idx)
        a, b, c = population[np.random.choice(indices, 3, replace=False)]
        mutant = a + F * (b - c)
        return np.clip(mutant, self.lb, self.ub)

    def crossover(self, target, mutant, CR):
        crossover_mask = np.random.rand(self.dim) < CR
        if not np.any(crossover_mask):
            crossover_mask[np.random.randint(0, self.dim)] = True
        return np.where(crossover_mask, mutant, target)

    def __call__(self, func):
        self.lb, self.ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        population = self.initialize_population(self.lb, self.ub)
        fitness = np.array([func(ind) for ind in population])
        evaluations = self.population_size

        while evaluations < self.budget:
            for i in range(self.population_size):
                F = 0.4 + 0.3 * np.random.rand()  # self-adaptive F
                CR = 0.8 + 0.2 * np.random.rand()  # self-adaptive CR

                mutant = self.mutate(i, population, F)
                trial = self.crossover(population[i], mutant, CR)

                trial_fitness = func(trial)
                evaluations += 1

                if trial_fitness < fitness[i]:
                    population[i] = trial
                    fitness[i] = trial_fitness

                if evaluations >= self.budget:
                    break

        best_idx = np.argmin(fitness)
        return population[best_idx], fitness[best_idx]
```

# Description: Introducing Differential Evolution with Adaptive Mutation and Local Search (DEAM-LS) which combines adaptive mutation strategies and local search intensification for efficient global optimization of photonic structures.
# Code:
```python
import numpy as np

class DEAM_LS:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.mutation_factor = 0.8
        self.crossover_probability = 0.7
        self.best_solution = None
        self.best_value = float('inf')

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def mutate(self, population):
        idxs = np.random.choice(self.population_size, 3, replace=False)
        a, b, c = population[idxs]
        return a + self.mutation_factor * (b - c)

    def crossover(self, target, mutant):
        cross_points = np.random.rand(self.dim) < self.crossover_probability
        if not np.any(cross_points):
            cross_points[np.random.randint(0, self.dim)] = True
        trial = np.where(cross_points, mutant, target)
        return trial

    def local_search(self, candidate, lb, ub):
        perturbation = (ub - lb) * (np.random.rand(self.dim) - 0.5) * 0.1
        new_candidate = candidate + perturbation
        return np.clip(new_candidate, lb, ub)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        population = self.initialize_population(lb, ub)
        evaluations = 0

        while evaluations < self.budget:
            new_population = []
            for target_idx in range(self.population_size):
                target = population[target_idx]
                mutant = self.mutate(population)
                mutant = np.clip(mutant, lb, ub)
                trial = self.crossover(target, mutant)
                trial_value = func(trial)
                evaluations += 1

                if trial_value < func(target):
                    new_population.append(trial)
                    if trial_value < self.best_value:
                        self.best_value = trial_value
                        self.best_solution = trial
                else:
                    new_population.append(target)

                if evaluations >= self.budget:
                    break

                if np.random.rand() < 0.1:  # Local search probability
                    local_candidate = self.local_search(new_population[-1], lb, ub)
                    local_value = func(local_candidate)
                    evaluations += 1
                    if local_value < func(new_population[-1]):
                        new_population[-1] = local_candidate
                        if local_value < self.best_value:
                            self.best_value = local_value
                            self.best_solution = local_candidate

            population = np.array(new_population)

        return self.best_solution, self.best_value
```

# Description: Introducing Adaptive Differential Evolution with Dynamic Neighborhood Search (ADE-DNS) that combines adaptive mutation strategies and dynamic neighborhood exploration for enhanced convergence in photonic structure optimization.
# Code:
```python
import numpy as np

class ADE_DNS:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 20
        self.mutation_factor = 0.5
        self.crossover_prob = 0.9
        self.best_solution = None
        self.best_value = float('inf')

    def initialize_population(self, lb, ub):
        return [lb + (ub - lb) * np.random.rand(self.dim) for _ in range(self.pop_size)]

    def mutate(self, population, idx, lb, ub):
        indices = list(range(self.pop_size))
        indices.remove(idx)
        a, b, c = np.random.choice(indices, 3, replace=False)
        mutant = population[a] + self.mutation_factor * (population[b] - population[c])
        return np.clip(mutant, lb, ub)

    def crossover(self, target, mutant):
        cross_points = np.random.rand(self.dim) < self.crossover_prob
        if not np.any(cross_points):
            cross_points[np.random.randint(0, self.dim)] = True
        trial = np.where(cross_points, mutant, target)
        return trial

    def adaptive_mutation(self, target_value, global_best_value):
        return self.mutation_factor * (target_value / global_best_value)

    def dynamic_neighborhood_search(self, population, idx, lb, ub):
        distances = [np.linalg.norm(population[idx] - population[j]) for j in range(self.pop_size)]
        sorted_indices = np.argsort(distances)
        for j in sorted_indices[:5]:  # Consider the 5 nearest neighbors
            if j != idx:
                trial = self.mutate(population, j, lb, ub)
                trial = self.crossover(population[j], trial)
                trial_value = self.evaluate(trial)
                if trial_value < self.best_value:
                    self.best_value = trial_value
                    self.best_solution = trial

    def evaluate(self, solution):
        return func(solution)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        population = self.initialize_population(lb, ub)
        while evaluations < self.budget:
            for i in range(self.pop_size):
                target = population[i]
                mutant = self.mutate(population, i, lb, ub)
                trial = self.crossover(target, mutant)
                trial_value = func(trial)
                evaluations += 1

                if trial_value < self.evaluate(target):
                    population[i] = trial
                    if trial_value < self.best_value:
                        self.best_value = trial_value
                        self.best_solution = trial

                if evaluations >= self.budget:
                    break

            for i in range(self.pop_size):
                self.dynamic_neighborhood_search(population, i, lb, ub)

        return self.best_solution, self.best_value
```

# Description: A Parallel-Inspired Quantum Swarm Optimization (PIQSO) that utilizes simultaneous multi-swarm dynamics and quantum tunneling to enhance convergence speed and global exploration in photonic structure optimization.
# Code: 
```python
import numpy as np

class PIQSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.swarm_size = 20
        self.num_swarms = 5
        self.swarms = []
        self.global_bests = []

    def initialize_swarm(self, lb, ub):
        swarm = []
        for _ in range(self.swarm_size):
            position = lb + (ub - lb) * np.random.rand(self.dim)
            swarm.append({'position': position, 'best_position': position, 'best_value': float('inf')})
        return swarm

    def update_particle_position(self, particle, local_best, lb, ub, exploration_factor):
        r1 = np.random.rand(self.dim)
        direction = np.sign(np.random.rand(self.dim) - 0.5)
        particle['position'] = local_best + exploration_factor * (r1 - 0.5) * np.abs(local_best - particle['position']) * direction
        particle['position'] = np.clip(particle['position'], lb, ub)

    def quantum_tunneling(self, particle, lb, ub, global_best_value):
        if np.random.rand() < 0.1:
            q_jump = np.random.normal(0, 1, self.dim) * (ub - lb) * 0.1
            particle['position'] += q_jump
            particle['position'] = np.clip(particle['position'], lb, ub)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        self.swarms = [self.initialize_swarm(lb, ub) for _ in range(self.num_swarms)]
        self.global_bests = [np.full(self.dim, np.inf) for _ in range(self.num_swarms)]
        global_best_value = float('inf')

        while evaluations < self.budget:
            for swarm_index, swarm in enumerate(self.swarms):
                for particle_index, particle in enumerate(swarm):
                    value = func(particle['position'])
                    evaluations += 1

                    if value < particle['best_value']:
                        particle['best_value'] = value
                        particle['best_position'] = particle['position'].copy()

                    if value < self.global_bests[swarm_index].get('value', float('inf')):
                        self.global_bests[swarm_index] = {'position': particle['position'].copy(), 'value': value}

                    if value < global_best_value:
                        global_best_value = value
                        self.best_solution = particle['position'].copy()

                    if evaluations >= self.budget:
                        break

                exploration_factor = 1.0 - evaluations / self.budget
                for particle_index, particle in enumerate(swarm):
                    self.update_particle_position(particle, self.global_bests[swarm_index]['position'], lb, ub, exploration_factor)
                    self.quantum_tunneling(particle, lb, ub, global_best_value)

            if evaluations >= self.budget:
                break

        return self.best_solution, global_best_value
```

# Description: Introducing a Hybrid Genetic Differential Evolution (HGDE) algorithm that combines genetic recombination and differential mutation strategies to enhance exploration and exploitation in photonic structure optimization.
# Code:
```python
import numpy as np

class HGDE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.crossover_rate = 0.7
        self.mutation_factor = 0.5

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def select_parents(self, population):
        return population[np.random.choice(self.population_size, 3, replace=False)]

    def mutation(self, target, donors, lb, ub):
        mutant = donors[0] + self.mutation_factor * (donors[1] - donors[2])
        return np.clip(mutant, lb, ub)

    def crossover(self, target, mutant):
        crossover_mask = np.random.rand(self.dim) < self.crossover_rate
        return np.where(crossover_mask, mutant, target)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        population = self.initialize_population(lb, ub)
        best_solution = None
        best_value = float('inf')

        while evaluations < self.budget:
            new_population = np.empty_like(population)
            for i in range(self.population_size):
                target = population[i]
                donors = self.select_parents(population)
                mutant = self.mutation(target, donors, lb, ub)
                trial = self.crossover(target, mutant)

                trial_value = func(trial)
                evaluations += 1

                if trial_value < func(target):
                    new_population[i] = trial
                else:
                    new_population[i] = target

                if trial_value < best_value:
                    best_value = trial_value
                    best_solution = trial.copy()

                if evaluations >= self.budget:
                    break

            population = new_population

        return best_solution, best_value
