    def update_memory_archive(self, solution, value):
        self.memory_archive.append((solution.copy(), value))
        self.memory_archive = sorted(self.memory_archive, key=lambda x: x[1])[:self.archive_size]

    def adaptive_exploration(self, lb, ub):
        if self.memory_archive:
            selected_solution, _ = self.memory_archive[np.random.randint(len(self.memory_archive))]
            perturbation = np.random.normal(0, 0.1, self.dim)
            candidate = selected_solution + perturbation * (ub - lb)
            return np.clip(candidate, lb, ub)
        else:
            return lb + (ub - lb) * np.random.rand(self.dim)

    def local_refinement(self, solution, lb, ub):
        perturbation = np.random.normal(0, 0.01, self.dim)
        new_solution = solution + perturbation * (ub - lb)
        return np.clip(new_solution, lb, ub)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        population = self.initialize_population(lb, ub)

        while evaluations < self.budget:
            candidate_solutions = []
            for _ in range(self.population_size):
                if evaluations < self.budget // 2:
                    candidate = self.adaptive_exploration(lb, ub)
                else:
                    candidate = self.local_refinement(self.best_solution, lb, ub)
                candidate_solutions.append(candidate)

            for solution in candidate_solutions:
                value = func(solution)
                evaluations += 1
                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = solution
                self.update_memory_archive(solution, value)

                if evaluations >= self.budget:
                    break

        return self.best_solution, self.best_value

import numpy as np

class QIPSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.population_size = 20
        self.alpha = 0.9  # Quantum probability amplitude
        self.beta = 0.1  # Perturbation factor

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def quantum_update(self, particle, lb, ub):
        random_vector = np.random.rand(self.dim)
        quantum_position = np.where(random_vector < self.alpha,
                                    particle + self.beta * np.random.normal(0, 1, self.dim),
                                    lb + (ub - lb) * np.random.rand(self.dim))
        return np.clip(quantum_position, lb, ub)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        population = self.initialize_population(lb, ub)

        while evaluations < self.budget:
            for i in range(self.population_size):
                if evaluations >= self.budget:
                    break

                quantum_particle = self.quantum_update(population[i], lb, ub)
                value = func(quantum_particle)
                evaluations += 1

                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = quantum_particle

                # Update the original particle position based on quantum mechanism
                population[i] = quantum_particle

        return self.best_solution, self.best_value

import numpy as np

class QIEA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.population_size = 20
        self.alpha = np.random.rand(self.population_size, self.dim)
        self.beta = np.sqrt(1 - np.square(self.alpha))
        self.rotation_angle = 0.01

    def observe(self):
        return np.where(np.random.rand(self.population_size, self.dim) < np.square(self.alpha), 1, -1)

    def rotate(self, solutions, lb, ub, evaluations):
        for i in range(self.population_size):
            for j in range(self.dim):
                if evaluations < self.budget * 0.5:
                    theta = np.random.uniform(-self.rotation_angle, self.rotation_angle)
                else:
                    theta = self.rotation_angle * (self.best_solution[j] - solutions[i][j])
                self.alpha[i, j] = self.alpha[i, j] * np.cos(theta) - self.beta[i, j] * np.sin(theta)
                self.beta[i, j] = np.sqrt(1 - np.square(self.alpha[i, j]))

            solutions[i] = np.clip(solutions[i], lb, ub)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        solutions = self.observe() * (ub - lb) / 2 + (ub + lb) / 2

        while evaluations < self.budget:
            for i in range(self.population_size):
                solution = solutions[i]
                value = func(solution)
                evaluations += 1
                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = solution

                if evaluations >= self.budget:
                    break

            self.rotate(solutions, lb, ub, evaluations)
            solutions = self.observe() * (ub - lb) / 2 + (ub + lb) / 2

        return self.best_solution, self.best_value

import numpy as np

class QIES:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.population_size = 10
        self.quantum_population = np.pi * np.random.rand(self.population_size, self.dim)

    def decode(self, quantum_state, lb, ub):
        binary_population = np.cos(quantum_state) ** 2
        solution = lb + (ub - lb) * binary_population
        return solution

    def quantum_rotation(self, quantum_state, direction_vector):
        return quantum_state + np.pi * direction_vector * np.random.rand(self.dim)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0

        while evaluations < self.budget:
            candidate_solutions = []
            for q_state in self.quantum_population:
                solution = self.decode(q_state, lb, ub)
                candidate_solutions.append(solution)

            for solution in candidate_solutions:
                value = func(solution)
                evaluations += 1
                if value < self.best_value:
                    self.best_value = value
                    self.best_solution = solution

                if evaluations >= self.budget:
                    break

            # Quantum-inspired rotation to explore new regions
            direction_vectors = np.random.choice([-1, 1], size=(self.population_size, self.dim))
            self.quantum_population = np.array([
                self.quantum_rotation(q, d) for q, d in zip(self.quantum_population, direction_vectors)
            ])

        return self.best_solution, self.best_value

