        for _ in range(self.initial_swarm_size):
            position = lb + (ub - lb) * np.random.rand(self.dim)
            velocity = np.random.rand(self.dim) * (ub - lb) * 0.1
            swarm.append({'position': position, 'velocity': velocity, 'best_position': position, 'best_value': float('inf')})
        return swarm

    def update_particle(self, particle, global_best, lb, ub, beta):
        r1, r2 = np.random.rand(), np.random.rand()
        mean_best = (particle['best_position'] + global_best) / 2
        phi = np.arccos(1 - 2 * np.random.rand(self.dim))
        direction = np.sign(np.random.rand(self.dim) - 0.5)

        particle['position'] = mean_best + beta * (r1 - 0.5) * np.abs(global_best - particle['position']) * np.tan(phi) * direction
        particle['position'] = np.clip(particle['position'], lb, ub)

    def randomized_mutation(self, particle, lb, ub, evaluation_ratio):
        if np.random.rand() < 0.5 * (1 - evaluation_ratio):
            mutation_vector = (ub - lb) * (np.random.rand(self.dim) - 0.5) * 0.1
            particle['position'] += mutation_vector
            particle['position'] = np.clip(particle['position'], lb, ub)

    def adaptive_swarm_size(self, evaluations):
        return max(5, int(self.initial_swarm_size * (1 - evaluations / self.budget) + 5))

    def dynamic_beta_scaling(self, evaluations):
        return 0.5 + 0.5 * (1 - evaluations / self.budget)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        self.swarms = self.initialize_swarm(lb, ub)
        global_best = None
        global_best_value = float('inf')

        while evaluations < self.budget:
            for particle_index in range(len(self.swarms)):
                particle = self.swarms[particle_index]
                value = func(particle['position'])
                evaluations += 1

                if value < particle['best_value']:
                    particle['best_value'] = value
                    particle['best_position'] = particle['position'].copy()

                if value < global_best_value:
                    global_best_value = value
                    global_best = particle['position'].copy()

                if evaluations >= self.budget:
                    break

            swarm_size = self.adaptive_swarm_size(evaluations)
            if len(self.swarms) > swarm_size:
                self.swarms = self.swarms[:swarm_size]

            beta = self.dynamic_beta_scaling(evaluations)
            for particle_index in range(len(self.swarms)):
                particle = self.swarms[particle_index]
                self.update_particle(particle, global_best, lb, ub, beta)
                self.randomized_mutation(particle, lb, ub, evaluations / self.budget)

        return global_best, global_best_value

import numpy as np

class HGDE:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = 20
        self.crossover_rate = 0.7
        self.mutation_factor = 0.5

    def initialize_population(self, lb, ub):
        return lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

    def select_parents(self, population):
        return population[np.random.choice(self.population_size, 3, replace=False)]

    def mutation(self, target, donors, lb, ub):
        mutant = donors[0] + self.mutation_factor * (donors[1] - donors[2])
        return np.clip(mutant, lb, ub)

    def crossover(self, target, mutant):
        crossover_mask = np.random.rand(self.dim) < self.crossover_rate
        return np.where(crossover_mask, mutant, target)

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        population = self.initialize_population(lb, ub)
        best_solution = None
        best_value = float('inf')

        while evaluations < self.budget:
            new_population = np.empty_like(population)
            for i in range(self.population_size):
                target = population[i]
                donors = self.select_parents(population)
                mutant = self.mutation(target, donors, lb, ub)
                trial = self.crossover(target, mutant)

                trial_value = func(trial)
                evaluations += 1

                if trial_value < func(target):
                    new_population[i] = trial
                else:
                    new_population[i] = target

                if trial_value < best_value:
                    best_value = trial_value
                    best_solution = trial.copy()

                if evaluations >= self.budget:
                    break

            population = new_population

        return best_solution, best_value

import numpy as np

class EHS:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.harmony_memory_size = 25
        self.harmony_memory = []
        self.hmcr = 0.9  # Harmony Memory Consideration Rate
        self.par = 0.3   # Pitch Adjustment Rate
        self.freq_range = 0.1

    def initialize_harmony_memory(self, lb, ub):
        for _ in range(self.harmony_memory_size):
            position = lb + (ub - lb) * np.random.rand(self.dim)
            value = float('inf')
            self.harmony_memory.append({'position': position, 'value': value})

    def update_harmony_memory(self, candidate):
        worst_harmony = max(self.harmony_memory, key=lambda x: x['value'])
        if candidate['value'] < worst_harmony['value']:
            self.harmony_memory.remove(worst_harmony)
            self.harmony_memory.append(candidate)

    def generate_new_harmony(self, lb, ub):
        new_harmony = np.zeros(self.dim)
        for i in range(self.dim):
            if np.random.rand() < self.hmcr:
                selected_harmony = self.harmony_memory[np.random.randint(self.harmony_memory_size)]
                new_harmony[i] = selected_harmony['position'][i]
                if np.random.rand() < self.par:
                    new_harmony[i] += self.freq_range * (np.random.rand() - 0.5)
            else:
                new_harmony[i] = lb[i] + (ub[i] - lb[i]) * np.random.rand()
        new_harmony = np.clip(new_harmony, lb, ub)
        return new_harmony

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        self.initialize_harmony_memory(lb, ub)

        while evaluations < self.budget:
            new_position = self.generate_new_harmony(lb, ub)
            new_value = func(new_position)
            evaluations += 1

            candidate = {'position': new_position, 'value': new_value}
            self.update_harmony_memory(candidate)

            if evaluations >= self.budget:
                break

        best_harmony = min(self.harmony_memory, key=lambda x: x['value'])
        return best_harmony['position'], best_harmony['value']

import numpy as np

class QIHS:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.best_solution = None
        self.best_value = float('inf')
        self.harmony_size = 20
        self.harmonies = []

    def initialize_harmony_memory(self, lb, ub):
        harmony_memory = []
        for _ in range(self.harmony_size):
            position = lb + (ub - lb) * np.random.rand(self.dim)
            harmony_memory.append({'position': position, 'value': float('inf')})
        return harmony_memory

    def update_harmony(self, harmony, global_best, lb, ub, harmony_consideration_rate, pitch_adjustment_rate):
        new_position = harmony['position'].copy()
        for i in range(self.dim):
            if np.random.rand() < harmony_consideration_rate:
                new_position[i] = np.random.choice([h['position'][i] for h in self.harmonies])
                if np.random.rand() < pitch_adjustment_rate:
                    phi = np.arccos(1 - 2 * np.random.rand())
                    direction = np.sign(np.random.rand() - 0.5)
                    new_position[i] += direction * np.tan(phi) * (global_best[i] - new_position[i])
            else:
                new_position[i] = lb[i] + (ub[i] - lb[i]) * np.random.rand()

        new_position = np.clip(new_position, lb, ub)
        harmony['position'] = new_position

    def __call__(self, func):
        lb, ub = np.array(func.bounds.lb), np.array(func.bounds.ub)
        evaluations = 0
        self.harmonies = self.initialize_harmony_memory(lb, ub)
        global_best = None
        global_best_value = float('inf')
        harmony_consideration_rate = 0.9
        pitch_adjustment_rate = 0.3

        while evaluations < self.budget:
            for harmony in self.harmonies:
                value = func(harmony['position'])
                evaluations += 1

                if value < harmony['value']:
                    harmony['value'] = value

                if value < global_best_value:
                    global_best_value = value
                    global_best = harmony['position'].copy()

                if evaluations >= self.budget:
                    break

            for harmony in self.harmonies:
                self.update_harmony(harmony, global_best, lb, ub, harmony_consideration_rate, pitch_adjustment_rate)

        return global_best, global_best_value
