        return np.clip(position + quantum_noise, 0, 1)

    def __call__(self, func):
        lb, ub = func.bounds.lb, func.bounds.ub
        self.positions = lb + (ub - lb) * np.random.rand(self.population_size, self.dim)

        for i in range(self.population_size):
            self.fitness[i] = func(self.positions[i])
            if self.fitness[i] < self.best_fitness:
                self.best_fitness = self.fitness[i]
                self.best_solution = self.positions[i]
            self.evaluations += 1
            if self.evaluations >= self.budget:
                return self.best_solution

        while self.evaluations < self.budget:
            for i in range(self.population_size):
                indices = np.random.choice(self.population_size, 3, replace=False)
                a, b, c = self.positions[indices]
                mutant = np.clip(a + self.scale_factor * (b - c), lb, ub)
                trial = np.copy(self.positions[i])

                cross_points = np.random.rand(self.dim) < self.crossover_rate
                if not np.any(cross_points):
                    cross_points[np.random.randint(0, self.dim)] = True
                trial[cross_points] = mutant[cross_points]

                trial_fitness = func(trial)
                if trial_fitness < self.fitness[i]:
                    self.positions[i] = trial
                    self.fitness[i] = trial_fitness

                    if trial_fitness < self.best_fitness:
                        self.best_fitness = trial_fitness
                        self.best_solution = trial

                if np.random.rand() < self.adaptive_threshold:
                    self.positions[i] = self.quantum_perturbation(self.positions[i])

                self.evaluations += 1
                if self.evaluations >= self.budget:
                    break

            self._adapt_population()

        return self.best_solution

    def _adapt_population(self):
        if self.evaluations % (self.budget // 5) == 0:
            self.scale_factor = np.random.uniform(0.4, 0.9)
            self.crossover_rate = np.random.uniform(0.7, 0.95)

import numpy as np

class QuantumGeneticHarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = max(10, 5 * dim)
        self.hmcr = 0.9  # Harmony Memory Consideration Rate
        self.par = 0.3  # Pitch Adjustment Rate
        self.qbit_population = np.random.rand(self.population_size, dim)
        self.fitness = np.full(self.population_size, float('inf'))
        self.gbest = None
        self.gbest_score = float('inf')
        self.evaluations = 0

    def quantum_to_real(self, qbit):
        return qbit * (self.ub - self.lb) + self.lb

    def _initialize_population(self, func):
        self.lb, self.ub = func.bounds.lb, func.bounds.ub
        for i in range(self.population_size):
            real_position = self.quantum_to_real(self.qbit_population[i])
            score = func(real_position)
            self.fitness[i] = score
            if score < self.gbest_score:
                self.gbest_score = score
                self.gbest = real_position
            self.evaluations += 1
            if self.evaluations >= self.budget:
                return

    def _crossover(self, parent1, parent2):
        mask = np.random.rand(self.dim) > 0.5
        offspring = np.where(mask, parent1, parent2)
        return offspring

    def _harmony_search(self, func):
        for _ in range(self.population_size):
            if np.random.rand() < self.hmcr:
                idx1, idx2 = np.random.choice(self.population_size, 2, replace=False)
                new_qbit = self._crossover(self.qbit_population[idx1], self.qbit_population[idx2])
            else:
                new_qbit = np.random.rand(self.dim)

            if np.random.rand() < self.par:
                adjustment = (np.random.rand(self.dim) - 0.5) * 0.05
                new_qbit = (new_qbit + adjustment) % 1.0

            new_real = self.quantum_to_real(new_qbit)
            new_score = func(new_real)
            worst_idx = np.argmax(self.fitness)

            if new_score < self.fitness[worst_idx]:
                self.qbit_population[worst_idx] = new_qbit
                self.fitness[worst_idx] = new_score
                if new_score < self.gbest_score:
                    self.gbest = new_real
                    self.gbest_score = new_score

            self.evaluations += 1
            if self.evaluations >= self.budget:
                return

    def __call__(self, func):
        self._initialize_population(func)
        while self.evaluations < self.budget:
            self._harmony_search(func)
        return self.gbest

import numpy as np

class QuantumEnhancedES:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = max(10, 5 * dim)
        self.positions = np.random.rand(self.population_size, dim)
        self.sigma = 0.1  # Step size for mutation
        self.evaluations = 0
        self.gbest = None
        self.gbest_score = float('inf')
        self.local_best_positions = np.zeros((self.population_size, dim))
        self.local_best_scores = np.full(self.population_size, float('inf'))

    def quantum_tunneling(self):
        q_tunnel_prob = 0.1  # Probability for applying quantum tunneling
        if np.random.rand() < q_tunnel_prob:
            return np.random.normal(0, self.sigma, self.dim)
        return np.zeros(self.dim)

    def _update_individual(self, idx, func):
        noise = np.random.normal(0, self.sigma, self.dim)
        new_pos = self.positions[idx] + noise + self.quantum_tunneling()
        new_pos = np.clip(new_pos, func.bounds.lb, func.bounds.ub)
        new_score = func(new_pos)

        if new_score < self.local_best_scores[idx]:
            self.local_best_positions[idx] = new_pos
            self.local_best_scores[idx] = new_score

        if new_score < self.gbest_score:
            self.gbest = new_pos
            self.gbest_score = new_score

        self.positions[idx] = new_pos
        self.evaluations += 1

    def adaptive_niching(self):
        threshold = 0.2 * self.dim
        for i in range(self.population_size):
            if np.linalg.norm(self.positions[i] - self.gbest) < threshold:
                self.positions[i] = func.bounds.lb + (func.bounds.ub - func.bounds.lb) * np.random.rand(self.dim)
                self.local_best_scores[i] = float('inf')

    def __call__(self, func):
        self.positions = func.bounds.lb + (func.bounds.ub - func.bounds.lb) * np.random.rand(self.population_size, self.dim)
        for i in range(self.population_size):
            score = func(self.positions[i])
            self.local_best_positions[i] = self.positions[i]
            self.local_best_scores[i] = score
            if score < self.gbest_score:
                self.gbest = self.positions[i]
                self.gbest_score = score
            self.evaluations += 1
            if self.evaluations >= self.budget:
                return self.gbest

        while self.evaluations < self.budget:
            for i in range(self.population_size):
                self._update_individual(i, func)
                if self.evaluations >= self.budget:
                    break
            self.adaptive_niching()

        return self.gbest

import numpy as np

class AQGA:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.population_size = max(10, 5 * dim)
        self.positions = np.random.rand(self.population_size, dim)
        self.fitness = np.full(self.population_size, float('inf'))
        self.best_solution = None
        self.best_fitness = float('inf')
        self.evaluations = 0
        self.quantum_mutation_rate = 0.05

    def quantum_mutation(self, parent):
        alpha = np.pi * np.random.rand(self.dim)
        beta = np.pi * np.random.rand(self.dim)
        quantum_bit = np.sin(alpha) * parent + np.cos(beta) * (1 - parent)
        return quantum_bit

    def tournament_selection(self):
        indices = np.random.choice(self.population_size, 3, replace=False)
        selected = indices[np.argmin(self.fitness[indices])]
        return selected

    def crossover(self, parent1, parent2):
        crossover_point = np.random.randint(1, self.dim)
        child = np.concatenate((parent1[:crossover_point], parent2[crossover_point:]))
        return child

    def _evaluate(self, individual, func):
        individual = np.clip(individual, func.bounds.lb, func.bounds.ub)
        score = func(individual)
        self.evaluations += 1
        return score

    def __call__(self, func):
        self.positions = func.bounds.lb + (func.bounds.ub - func.bounds.lb) * np.random.rand(self.population_size, self.dim)
        for i in range(self.population_size):
            self.fitness[i] = self._evaluate(self.positions[i], func)
            if self.fitness[i] < self.best_fitness:
                self.best_fitness = self.fitness[i]
                self.best_solution = self.positions[i]
            if self.evaluations >= self.budget:
                return self.best_solution

        while self.evaluations < self.budget:
            new_positions = []
            for _ in range(self.population_size):
                if np.random.rand() < self.quantum_mutation_rate:
                    parent = self.positions[self.tournament_selection()]
                    child = self.quantum_mutation(parent)
                else:
                    parent1 = self.positions[self.tournament_selection()]
                    parent2 = self.positions[self.tournament_selection()]
                    child = self.crossover(parent1, parent2)

                new_positions.append(child)

            for i in range(self.population_size):
                new_score = self._evaluate(new_positions[i], func)
                if new_score < self.fitness[i]:
                    self.positions[i] = new_positions[i]
                    self.fitness[i] = new_score

                if new_score < self.best_fitness:
                    self.best_fitness = new_score
                    self.best_solution = new_positions[i]

                if self.evaluations >= self.budget:
                    break

            if self.evaluations % (self.budget // 5) == 0:
                self.quantum_mutation_rate = min(0.2, self.quantum_mutation_rate + 0.01)

        return self.best_solution
